fileDef.fileContent:
"""
Usage:
    group_by_metadata.py

Description:
    Prepare Input Files manipulates / creates an output list as group of files with the same metadata.

Options:

    -h, --help            Show this message.

    -v, -V, --version     Tool version.
"""

import json

job = ""
with open('job.json') as data_file:
    job = json.load(data_file)

if "inputs" in job and "input_files" in job["inputs"]:
    input_files = job["inputs"]["input_files"]
else:
    input_files = []

files_1 = []
files_2 = []

for file_list in input_files:
    if file_list and not isinstance(file_list, list):
        f=file_list
        d = {'class': 'File'}
        d['path'] = f["path"]
        print f
        if "size" in f:
            d['size'] = f["size"]

        if "contents" in f:
            d['contents'] = f["contents"]

        if "name" in f:
            d['name'] = f["name"]

        if "checksum" in f:
            d['checksum'] = f["checksum"]

        if "location" in f:
            d['location'] = f["location"]

        if "metadata" in f:
            d['metadata'] = f["metadata"]

        if "secondaryFiles" in f:
            d['secondaryFiles'] = f["secondaryFiles"]

        if "metadata" in f and "paired_end" in f["metadata"] and f["metadata"]["paired_end"] == "1":
            files_1.append(d)
        else:
            files_2.append(d)

    elif file_list:
        for f in file_list:
            print(f)
            d = {'class': 'File'}
            d['path'] = f["path"]

            if "size" in f:
                d['size'] = f["size"]

            if "contents" in f:
                d['contents'] = f["contents"]

            if "name" in f:
                d['name'] = f["name"]

            if "checksum" in f:
                d['checksum'] = f["checksum"]

            if "location" in f:
                d['location'] = f["location"]

            if "metadata" in f:
                d['metadata'] = f["metadata"]

            if "secondaryFiles" in f:
                d['secondaryFiles'] = f["secondaryFiles"]

            if "metadata" in f and "paired_end" in f["metadata"] and f["metadata"]["paired_end"] == "1":
                files_1.append(d)
            else:
                files_2.append(d)

data = {}
if files_1:
    data['output_files_1'] = files_1

if files_2:
    data['output_files_2'] = files_2

with open('cwl.output.json', 'w') as w:
    json.dump(data, w)

