reference_genome.script:
{
  var ref_name = $job.inputs.reference_or_index.path.split("/").pop().replace(/\.[^/.]+$/, "")
//  var inherited_name = $job.inputs.reference_or_index.metadata.reference_genome
  var ref_ext = $job.inputs.reference_or_index.path.split('.').pop(0).toUpperCase()
  if (ref_ext == "FASTA" || ref_ext == "FA" ||
      $job.inputs.reference_or_index.metadata == null ||
      $job.inputs.reference_or_index.metadata.reference_genome == null ||
      $job.inputs.reference_or_index.metadata.reference_genome == ""){
    return ref_name
  } else{
    return $job.inputs.reference_or_index.metadata.reference_genome
  }
}

baseCommand.script:
{
  var ref_ext = $job.inputs.reference_or_index.path.split('.').pop(0).toUpperCase()
  if (ref_ext == "FASTA" || ref_ext == "FA"){
    return "bowtie2-build -f"
  } else {
    return "echo 'Archive provided on the input, no indexing.'"
  }
}

valueFrom.script:
{
  index_prefix = $job.inputs.reference_or_index.path.split('.').slice(0, $job.inputs.reference_or_index.path.split('.').length-1).join('.').replace(/^.*[\\\/]/, '')
  var ref_ext = $job.inputs.reference_or_index.path.split('.').pop(0).toUpperCase()
  if (ref_ext == "FASTA" || ref_ext == "FA"){
    return "./".concat(index_prefix)
  }
}

{
  var bt = "bowtie2-2.3.4.1"
  var suffix_glob = ""
  var ref = $job.inputs.reference_or_index.path
  var ref_name = ref.split('/').pop(0).replace(/\.[^/.]+$/, "")
  var tar_name = ref_name.concat("_", bt, '.tar')
  var ref_ext = $job.inputs.reference_or_index.path.split('.').pop(0).toUpperCase()

  if ($job.inputs.large_index) {
    suffix_glob = " ./*.bt2l*"
  } else {
    suffix_glob = " ./*.bt2*"
  }
  if (ref_ext == "FASTA" || ref_ext == "FA"){
    return " && tar -vcf ".concat(tar_name, suffix_glob)
  }
}

{
  var param = $job.inputs.suffixes_as_fraction
  var ref_ext = $job.inputs.reference_or_index.path.split('.').pop(0).toUpperCase()
  if ( param && (ref_ext == "FASTA" || ref_ext == "FA")){
    if ($job.inputs.disable_default_parameters && $job.inputs.suffixes_as_fraction) {
      return "--bmaxdivn ".concat($job.inputs.suffixes_as_fraction)
    }
  }
}

{
  var param = $job.inputs.suffixes
  var ref_ext = $job.inputs.reference_or_index.path.split('.').pop(0).toUpperCase()
  if ( param && (ref_ext == "FASTA" || ref_ext == "FA")){
    if ($job.inputs.disable_default_parameters && $job.inputs.suffixes) {
      return "--bmax ".concat($job.inputs.suffixes)
    }
  }
}

{
  var param = $job.inputs.seed
  var ref_ext = $job.inputs.reference_or_index.path.split('.').pop(0).toUpperCase()
  if ( param && (ref_ext == "FASTA" || ref_ext == "FA")){
    return "--seed"
  }
}

{
  var param = $job.inputs.rows_to_mark
  var ref_ext = $job.inputs.reference_or_index.path.split('.').pop(0).toUpperCase()
  if ( param && (ref_ext == "FASTA" || ref_ext == "FA")){
