{
  	output = ""
	ext = [].concat($job.inputs.fastq_files)[0].path.split('.').pop()

	if(ext == 'gz' || ext == 'GZ')
    {	
      	fastq_list = [].concat($job.inputs.fastq_files)
      	output = "gunzip -f " 
        for(i=0; i < fastq_list.length; i++)
        	output += fastq_list[i].path.split('/').pop() + " "
        output += "&& python mode.py -fq "

        for(i=0; i < fastq_list.length; i++){

          	file_name = fastq_list[i].path.split('/').pop().split(".").slice(0, -1).join(".")
        	output += file_name + " "
        }

      	if($job.inputs.ntrim)
        {
        	output += "-t " + $job.inputs.ntrim
        }
    }
  	else
    {
    	output = "python mode.py -fq "
        fastq_list = [].concat($job.inputs.fastq_files)
        for(i=0; i < fastq_list.length; i++){

          	file_name = fastq_list[i].path.split('/').pop()
            output += file_name + " "
        }
      	if($job.inputs.ntrim)
        {
        	output += "-t " + $job.inputs.ntrim
        }
    }
	return output
}

