fileDef.fileContent:
import json
from collections import defaultdict
from copy import deepcopy
from os.path import basename, abspath

# constants
INPUTS_KEY = 'inputs'
METADATA_KEY = 'metadata'
NAME_KEY = 'name'
PATH_KEY = 'path'
CLASS_KEY = 'class'
LOCATION_KEY = 'location'
UNCATEGORIZED = 'uncategorized'

class CWLFile(object):
    _metadata = None
    _path = None

    @property
    def metadata(self):
        return self._metadata

    @metadata.setter
    def metadata(self, value):
        self._metadata = value

    @metadata.deleter
    def metadata(self):
        del self._metadata

    @property
    def path(self):
        return self._path

    @path.setter
    def path(self, value):
        self._path = value

    @path.deleter
    def path(self):
        del self._path

    def __str__(self):
        return self.path if self.path else ''

    def __repr__(self):
        return self.__str__()

    def __init_str__(self, file: str):
        self.metadata = {}
        self.path = abspath(file)

    def __init_dict__(self, file: dict):
        self.metadata = deepcopy(file['metadata']) if file['metadata'] else {}
        self.path = file['path'] if file['path'] else ''

    def __init__(self, *args, **kwargs):
        if (len(args) > 0):
            if isinstance(args[0], str):
                self.__init_str__(args[0])
            elif isinstance(args[0], dict):
                self.__init_dict__(args[0])
            else:
                raise Exception("Can\'t make instance of class CWLFile. "
                                "Argument have to be either instance of type str or dict. ")


    def intersect_metadata(self, m2: dict):
        """
        :param m2: Metadata dictionary
        :return: None
        """
        self.metadata = {k: v for k, v in self.metadata.items() if v == m2.get(k)}

    def toJSON(self):
        dict = {
            CLASS_KEY: 'File',
            PATH_KEY: self.__str__(),
            NAME_KEY: basename(self.__str__()),
            METADATA_KEY: self.metadata
        }
        return dict

class CWLGroup(dict):

    def _leafs(self, obj, out: list):
        """
        :param obj: CWLGroup node
        :param out: Reference of output list
        :return: None
        """
        if isinstance(obj, list):
            out.append(obj)
        elif isinstance(obj, dict):
            for _,value in obj.items():
                self._leafs(value, out)
        else:
            raise Exception('Unexpected type to be flatten.')

    def leafs(self):
        """
        :return: List of groups located on leafs of grouped tree.
        """
        out = list()
        self._leafs(self, out)
        return out


class CWL(object):

    #private
    _inputs = None
    _job_json_path = ''
    _cwl_output_json_path = ''

    #properties
    @property
    def inputs(self):
        return self._inputs

    @property
    def job_json_path(self):
        return self._job_json_path

    @job_json_path.setter
    def job_json_path(self, value):
        self._job_json_path = value

    @job_json_path.deleter
    def job_json_path(self):
        del self._job_json_path

    @property
    def cwl_output_json_path(self):
        return self._cwl_output_json_path

    @cwl_output_json_path.setter
    def cwl_output_json_path(self, value):
        self._cwl_output_json_path = value

    @cwl_output_json_path.deleter
    def cwl_output_json_path(self):
        del self._cwl_output_json_path


    #methods
    def __init__(self, job_json: str='job.json'):
        self._inputs = dict()
        self.parse_job_json(job_json_path=job_json)

    def parse_job_json(self, job_json_path='job.json', key: str=None):
        """
        :param job_json_path: Location of job.json file
        :param key: Extract only specific key from job.json inputs
        :return: None
        """
        self.job_json_path = job_json_path
        try:
            with open(self.job_json_path) as job_json_file:
                job_json = json.load(job_json_file)
                if job_json and INPUTS_KEY in job_json:
                    if key is None:
                        for key in job_json[INPUTS_KEY]:
                            self._inputs[key] = job_json[INPUTS_KEY][key]
                    elif key in job_json[INPUTS_KEY]:
                        inputs = job_json[INPUTS_KEY][key]
                        self._inputs = inputs
                    else:
                        raise Exception('Key '+ key +' is not member of inputs property.')
        except IOError:
            raise Exception('ERROR parse_fastq: job.json file doesn\'t exists')



    def full_group_by(self, l, key=lambda x: x):
        """
        :param l: List that will be grouped
        :param key: Function used for creating keys in new Group 
        :return: Key, Value pairs
        """
        d = defaultdict(list)
        for item in l:
            k = key(item)
            if k is not None:
                d[k].append(item)
            else:
                d[UNCATEGORIZED].append(item)
        return d.items()

    def group_by_metadata_key(self, metadata_key: str, inputs: list) -> CWLGroup:
        """
        :param metadata_key: Key that is used for grouping
        :param inputs: List of inputs that will be grouped
        :return: Instance of CWLGroup after grouping by metadata_key
        """
        return CWLGroup({key: [f for f in val]
                for key, val in self.full_group_by(inputs, key=lambda x: x[METADATA_KEY][metadata_key]
                                                                        if metadata_key in x[METADATA_KEY]
                                                                        else None)})
    def group_by(self, metadata_keys: list, input_key, sort_by_metadata_key: str=None) -> CWLGroup:
        """
        :param metadata_keys: The keys that are used for grouping
        :param input_key: input key in job.json inputs field
        :param sort_by_metadata_key: Key used for sorting leafs
        :return: Instance of CWLGroup after grouping by metadata_keys
        """
        if isinstance(metadata_keys,list) and len(metadata_keys) > 0:
            if input_key in self._inputs:
                groups = self.group_by_metadata_key(metadata_key=metadata_keys[0],
                                                    inputs=deepcopy(self._inputs)[input_key])
                last_groups = [groups]
                for i in range(1, len(metadata_keys)):
                    metadata_key = metadata_keys[i]
                    newGroups = list()
                    for group in last_groups:
                        for group_key in group:
                            g = self.group_by_metadata_key(metadata_key=metadata_key, inputs=group[group_key])
                            group[group_key] = g
                            newGroups.append(group[group_key])
                    last_groups = newGroups

                if sort_by_metadata_key:
                    for group in last_groups:
                        for key in group:
                            group[key].sort(key=lambda x: x[METADATA_KEY][sort_by_metadata_key])
                return groups
            else:
                raise Exception('Error: ' + input_key + ' not member of ', self._inputs)
        else:
            raise Exception('Error: metadata_keys argument have to be non empty list.')

    def create_out_json(self, file: CWLFile) -> dict:
        """
        :param file: CWLFile object 
        :return: JSON representation of file 
        """
        out = {}
        out[CLASS_KEY] = 'File'
        out[PATH_KEY] = file.__str__()
        out[NAME_KEY] = basename(file.__str__())
        out[METADATA_KEY] = file.metadata
        return out

    def create_cwl_file(self, path: str, metadata: dict):
        """
        :param path: Path to cwl file 
        :param metadata: Metadata information about file
        :return: CWLFile
        """
        cwl_file = CWLFile(path)
        cwl_file.metadata = metadata
        return cwl_file
    def make_cwl_output_json(self, out_path: str, cwl_files: dict):
        """
        :param out_path: Location where cwl.output.json will be created.
        :param cwl_files: Files with metadata that will be written into cwl.output.json in the header field.
        :param output_id: Output id in cwl.output.json
        :return: None
        """
        try:

            with open(out_path, 'w') as out:
                json.dump(cwl_files, out, default=lambda o: o.toJSON(), sort_keys=True, indent=4, separators=(',', ': '))

        except Exception as e:
            print ('Error: ' + str(e))

from abc import ABCMeta, abstractmethod
from typing import List
import subprocess

class Compressor(metaclass=ABCMeta):

    @abstractmethod
    def get_compress_cmd(self, params: dict = None) -> List[str]:
        pass

    @abstractmethod
    def get_decompress_cmd(self, params: dict = None) -> List[str]:
        pass

    def compress(self, stdin, stdout, params: dict=None):
        if not stdout:
            raise Exception('Need to specify stdout stream')
        subprocess.call(self.get_compress_cmd(params), stdin=stdin, stdout=stdout)

    def decompress(self, stdin, stdout, params: dict=None):
        if not stdout:
            raise Exception('Need to specify stdout stream')
        subprocess.call(self.get_decompress_cmd(params), stdin=stdin, stdout=stdout)

class GzipCompressor(Compressor):
    def get_compress_cmd(self, params: dict= None) -> List[str]:
        return ['gzip', '--force', '-c']

    def get_decompress_cmd (self, params: dict= None) -> List[str]:
        return ['gzip', '--decompress', '--force', '-c']

class PigzCompressor(Compressor):
    def get_compress_cmd(self, params: dict = None) -> List[str]:
        cmd = ['pigz', '--force', '--keep']
        if params and params['threads'] and isinstance(params['threads'], int):
            cmd.extend(['-p', str(params['threads'])])
        return cmd

    def get_decompress_cmd (self, params: dict = None) -> List[str]:
        return ['pigz', '--decompress', '--force', '--keep']

