     }
    }
}

if (reads1.length > 0 & reads1.length == reads2.length){
	cmd = "-1 " + reads1.join(",") + " -2 " + reads2.join(",");
}
if (u_reads.length > 0){
    cmd = " -U " + u_reads.join(",");
}


return cmd
}

